import{_ as t,c as o,o as a,ag as i}from"./chunks/framework.BeMlo_ux.js";const h=JSON.parse('{"title":"Visual Studio 2019 C#中远程调试","description":"","frontmatter":{"title":"Visual Studio 2019 C#中远程调试","date":"2020-03-20T12:22:32.000Z","outline":"deep"},"headers":[],"relativePath":"microsoft/2020-03-20-msdn-ide-debugger-remote.md","filePath":"microsoft/2020-03-20-msdn-ide-debugger-remote.md","lastUpdated":1747924457000}'),r={name:"microsoft/2020-03-20-msdn-ide-debugger-remote.md"};function s(l,e,d,u,n,m){return a(),o("div",null,e[0]||(e[0]=[i('<h3 id="下载和安装" tabindex="-1">下载和安装 <a class="header-anchor" href="#下载和安装" aria-label="Permalink to &quot;下载和安装&quot;">​</a></h3><p><a href="https://visualstudio.microsoft.com/downloads#remote-tools-for-visual-studio-2019" target="_blank" rel="noreferrer">Visual Studio 2019</a></p><p><a href="https://my.visualstudio.com/Downloads?q=remote%20tools%20visual%20studio%202017" target="_blank" rel="noreferrer">Visual Studio 2017</a></p><p><a href="https://my.visualstudio.com/Downloads?q=remote%20tools%20visual%20studio%202015" target="_blank" rel="noreferrer">Visual Studio 2015</a></p><h3 id="设置远程调试器" tabindex="-1">设置远程调试器 <a class="header-anchor" href="#设置远程调试器" aria-label="Permalink to &quot;设置远程调试器&quot;">​</a></h3><ol><li>目标机器上安装远程调试工具，右击远程调试器应用，然后选择以管理员身份运行 <img src="https://docs.microsoft.com/zh-cn/visualstudio/debugger/media/remotedebuggerconfwizardpage.png?view=vs-2019" alt="view"></li><li>选择配置远程调试，配置防火墙并启动远程调试器</li><li>配置完成后，远程调试器窗口会显示 <img src="https://docs.microsoft.com/zh-cn/visualstudio/debugger/media/remotedebuggerwindow.png?view=vs-2019" alt="view"></li><li>远程调试器现在正在等待连接。 使用服务器名称和端口号显示在 Visual Studio 中设置的远程连接配置。</li></ol><h3 id="远程调试项目" tabindex="-1">远程调试项目 <a class="header-anchor" href="#远程调试项目" aria-label="Permalink to &quot;远程调试项目&quot;">​</a></h3><ol><li>在“解决方案资源管理器”中，右键单击项目，然后选择“属性”。</li><li>在“属性”页上，选择&quot;调试&quot;选项卡。 <img src="https://docs.microsoft.com/zh-cn/visualstudio/debugger/media/remotedebuggercsharp.png?view=vs-2019" alt="view"></li><li>请确保“工作目录”文本框为空。</li><li>选择 &quot;使用远程计算机&quot;，然后在文本框中键入yourmachinename： port （端口号显示在远程调试器窗口中。 每个版本的 Visual Studio 中的端口号递增2。例如：MJO-DL： Visual Studio 2019 上的4024</li><li>请确保未选中“启用本机代码调试”。</li><li>生成项目,请确保远程调试器正在目标计算机上运行</li><li>在 Visual Studio 中，开始调试</li></ol><h3 id="相关资料" tabindex="-1">相关资料 <a class="header-anchor" href="#相关资料" aria-label="Permalink to &quot;相关资料&quot;">​</a></h3><p><a href="https://docs.microsoft.com/zh-cn/visualstudio/debugger/remote-debugging-csharp?view=vs-2019" target="_blank" rel="noreferrer">MSDN C#中远程调试</a><br><a href="https://zhuanlan.zhihu.com/p/29183052" target="_blank" rel="noreferrer">远程调试</a></p>',10)]))}const g=t(r,[["render",s]]);export{h as __pageData,g as default};
